mapper.select:
SELECT t.id, t.deal_serial, t.deal_type AS dealTypeCode, CASE t.deal_type WHEN 1 THEN '协商出租' WHEN 2 THEN '一事一议' WHEN 3 THEN '挂靠' END AS dealType, t.pay_type AS payTypeCode, CASE t.pay_type WHEN 1 THEN '一月一付' WHEN 3 THEN '一季度一付' WHEN 6 THEN '半年一付' WHEN 12 THEN '一年一付' END AS payType, group_concat(CONCAT(t3.name,t1.park,ifnull(t1.build_no,'/'),'栋',ifnull(t1.build_level,'/'),'层',ifnull(t1.build_room,'')) SEPARATOR ',') AS location, t2.fk_own_id AS lessorId, t4.name AS lessor, t.fk_renter_id AS renterId, t5.name AS renter, t.rent_month, t.free_rent_month, t.is_new_rent AS isNewRentCode, CASE t.is_new_rent WHEN 1 THEN '新签' WHEN 2 THEN '续签' END AS isNewRent, t.is_have_discount AS isHaveDiscountCode, CASE t.is_have_discount WHEN 1 THEN '有优惠' WHEN 2 THEN '无优惠' END AS isHaveDiscount, t.deal_exist_status AS dealExistStatusCode, CASE t.deal_exist_status WHEN 1 THEN '进行中' WHEN 2 THEN '已删除' WHEN 3 THEN '正常终止' WHEN 4 THEN '提前结束' END AS dealExistStatus, t.deal_review_status AS dealReviewStatusCode, CASE t.deal_review_status WHEN 1 THEN '未审核' WHEN 2 THEN '通过审核' WHEN 3 THEN '未通过审核' END AS dealReviewStatus, t.start_time, t.end_time FROM deal t LEFT JOIN mid_deal_house t11 ON t11.fk_deal_id = t.id LEFT JOIN house_resource t1 ON t11.fk_house_resource_id = t1.id LEFT JOIN house_assets t2 ON t1.fk_house_assets_id = t2.id LEFT JOIN dic_agency t3 ON t2.fk_agency_id = t3.id LEFT JOIN dic_assets_co t4 ON t2.fk_own_id = t4.id LEFT JOIN renter t5 ON t.fk_renter_id = t5.id LEFT JOIN land_assets t6 ON t2.fk_land_assets_id = t6.id WHERE 1 = 1
AND t1.fk_agency_id = #{cd.aid}
AND t1.fk_agency_id = #{cd.agencyId}
AND t.deal_name = #{cd.dealName}
AND t3.id = #{cd.lessorId}
AND t5.id = #{cd.renterId}
AND t.deal_exist_status = #{cd.dealExistStatusCode}
AND t.deal_review_status = #{cd.dealReviewStatusCode}
AND t.deal_type = #{cd.dealTypeCode}
AND t2.fk_house_usage = #{cd.houseUsageId}
AND t3.id = #{cd.agencyId}
AND t.pay_type = #{cd.payTypeCode}
AND to_days(date(t.start_time) ) >= to_days(date(#{cd.startTime}))
AND to_days(date(t.end_time )) <= to_days(date(#{cd.endTime}))
AND t1.resource_area >= #{cd.minResourceArea}
AND t1.resource_area <= #{cd.maxResourceArea}
AND TIMESTAMPDIFF(MONTH,t.start_time,t.end_time) >= #{cd.minRentMonth}
AND TIMESTAMPDIFF(MONTH,t.start_time,t.end_time) <= #{cd.maxRentMonth}
AND t1.origin_rent_charge >= #{cd.minOriginRentCharge}
AND t1.origin_rent_charge <= #{cd.maxOriginRentCharge}
AND t1.guide_rent_charge >= #{cd.minGuideRentCharge}
AND t1.guide_rent_charge <= #{cd.maxGuideRentCharge}
GROUP BY t.id
ORDER BY t.created_time DESC
ORDER BY t.created_time ASC
